      {
        update: (cache, { data }) => {
          const { me: currentUserData } = cache.readQuery({
            query: CURRENT_USER_QUERY,
          });
          cache.writeQuery({
            query: CURRENT_USER_QUERY,
            data: { me: { ...currentUserData, ...data.associateWallet } },
          });
        },
        onError: () => {
          toast.error("ðŸ˜³ Ekkk! Failed to link wallet.", {
            position: "bottom-left",
          });
        },
      }
    );

    const connectActiveWalletToLyraLabs = async () => {
      associateWallet({
        variables: {
          address: walletUser.addr,
        },
      });
    };

    return (
      <WalletContext.Provider
        value={{
          status,
          setStatus,
          transaction,
          setTransaction,
          walletConnected,
          walletNotConnected,
          setupWallet,
          setupFCLAuthHandler,
          walletUser,
          vaultIsSetup,
          collectionIsSetup,
          walletBalance,
          walletCollection,
          connectActiveWalletToLyraLabs,
          giveAward,
          authHandlerIsSetup,
          setAuthHandlerIsSetup,
        }}
      >
        <Component />
      </WalletContext.Provider>
    );
  };
  return WithWallet;
};
